mapper.update:
update message
set status = 1
where id = #{messageId};

mapper.select:
select *
from message
where to_id = #{userId}
  and type = #{type}
order by create_time desc
Limit #{limit} Offset #{offset};

select count(*)
from message
where to_id = #{userId}
  and type = #{type}

select count(*)
from message
where to_id = #{userId}
  and type = #{type}
  and status = 0;
